function processShortText(str){
    if (str.length >= 123){
        return str.substr(0,120)+"...";
    } else {
        return str;
    }
}

function processEmailForm() {


    var errors = [];

// validate the name field
    var emailers_name = $('#emailers_name').val();
    var email_to = $('#email_to').val();
    var email_message = $('#email_message').val();
    var section_name = $('#email_section_name').text();
    var section_img = $('#section_image').prop("src");
    var email_section_text = $('#email_section_text_full').text();
    var email_shortlink = $('#email_shortlink').text();

    if(email_message.trim() == "Type a message..."){
        email_message = "";
    }

    if (emailers_name.trim() == "Sender's Name" || emailers_name.trim().length < 2) {

        errors.push({field: "name", error: "Your name"});
    }

    var email_regex = /[a-z0-9!#$%&'*+/=?^_`{|}~-]+(?:\.[a-z0-9!#$%&'*+/=?^_`{|}~-]+)*@(?:[a-z0-9](?:[a-z0-9-]*[a-z0-9])?\.)+[a-z0-9](?:[a-z0-9-]*[a-z0-9])?/;
    var email_arr = email_regex.exec(email_to);

    if (email_to.trim() == "Recipient's Email Address" || email_to.trim().length < 4 || !email_arr) {

        errors.push({field: "email", error: "A valid email address"});
    }


    if (errors.length == 0) {



        showSendingNotification();
        // no errors
        // process the form
        var message_object = { "section_img": section_img, "emailers_name": emailers_name, "email_to": email_to, "email_message": email_message, "section_name": section_name, "email_shortlink":email_shortlink, "email_section_text":email_section_text };


        var jqxhr = $.post("http://unhcrmedia-01.herokuapp.com/send-future-of-syria-email", message_object, function (data) {

            //console.log("data", data);
            if (data.hasOwnProperty("success")) {

                showMessageSuccessNotfication();

                //location.replace("call-to-action.html#message-success");

            } else {

                showEmailErrorNotification();
                //$.fancybox.open($(".connection_error"), {});

            }
        })
            .done(function () {
                // console.log(".done" );
            })
            .fail(function (error) {
                $.fancybox.open($(".connection_error"), {});
                //console.log(".fail", error);
            })
            .always(function () {
                //console.log( ".finished" );
            });


    } else {


        var error_str = "Please enter ";



        for (var i = 0; i < errors.length; i++){
            if (errors[i].field == "name"){
                error_str += "your name";
            } else if (errors[i].field == "email"){
                if (error_str == "Please enter your name"){
                    error_str += " and a valid email address";
                } else {
                    error_str += "a valid email address."
                }
            }
        }

        $('#error_message p').text(error_str);

       showErrorMessagesNotification();


    }


}

function showSendingNotification(){
//console.log("showSendingNotification");

    $('#status_message').show();
    $('#email_sending').fadeIn();

}


function showMessageSuccessNotfication(){

    //console.log("showMessageSuccessNotfication");

    if ($('#status_message').is(':visible') == false){

       $('#status_message').show();
    }

    if ($('#email_sending').is(":visible") == true){
        $('#email_sending').fadeOut("fast");
    }

    $('#email_sent').fadeIn("slow");

}


function showEmailErrorNotification(){

    console.log("in showEmailErrorNotification ");
   // console.log("showEmailErrorNotification");

    if ($('#status_message').is(':visible') == false){
        console.log("is not visible");
        $('#status_message').show();
    }

    if ($('#email_sending').is(":visible") == true){
        $('#email_sending').fadeOut("fast");
    }

    $('#email_error').fadeIn("slow");
}


function showErrorMessagesNotification(){

    //console.log("showErrorMessagesNotification");

    if ($('#status_message').is(':visible') == false){
        $('#status_message').show();
    }


    $('#error_message').fadeIn("fast");
    $("#error_ok_button").click(function(e){
        e.preventDefault();
        $('#error_message').fadeOut("fast", function(){
            $('#status_message').fadeOut("fast");
        });
    });
}

function closeAllEmailNotifications(){
    //console.log("closing all");
    $('#email_sending').hide();
    $('#email_error').hide();
    $('#email_sent').hide();
    $('#status_message').hide();
    $('#emailers_name').val("Sender's Name");
    $('#email_to').val("Recipient's Email Address");
    $('#email_message').val('Type a message...');
    $('#message_sent').hide();
    $('#message_form_error').hide();
    $('#message_send_error').hide();
}